import React from 'react';
import { Bell, User, Star, Calendar, BookOpen } from 'lucide-react';
import logo from '../logo/nujoom-logo.png';

const Header = ({ navigateToPage, currentPage }) => {
  console.log("Current page:", currentPage); // Debug current page

  return (
    <div className="bg-white border-b">
      <div className="container mx-auto px-6 py-3">
        <div className="flex justify-between items-center">
          <div className="flex items-center">
            <div 
              className="cursor-pointer flex items-center ml-8"
              onClick={() => navigateToPage('mainPage')}
            >
              <img src={logo} alt="نجوم" className="h-16" />
            </div>

            <nav className="flex flex-wrap">
              <div 
                className={`cursor-pointer ml-6 mb-2 ${currentPage === 'playersPage' ? 'text-green-700 font-medium' : 'text-gray-600'}`}
                onClick={() => navigateToPage('playersPage')}
              >
                المواهب
              </div>
              <div 
                className={`cursor-pointer ml-6 mb-2 ${currentPage === 'comparisons' ? 'text-green-700 font-medium' : 'text-gray-600'}`}
                onClick={() => navigateToPage('comparisons')}
              >
                المقارنات
              </div>
              <div 
              //  className={`cursor-pointer ml-6 mb-2 ${currentPage === 'matchAnalysis' ? 'text-green-700 font-medium' : 'text-gray-600'}`}
                //onClick={() => navigateToPage('matchAnalysis')}

                className={`cursor-pointer ml-6 mb-2 ${currentPage === 'calendar' ? 'text-green-700 font-medium' : 'text-gray-600'}`}
                onClick={() => navigateToPage('calendar')}
              >
                جدول المباريات
              </div>
              <div 
                className={`cursor-pointer ml-6 mb-2 ${currentPage === 'shortlistedPlayers' ? 'text-green-700 font-medium' : 'text-gray-600'}`}
                onClick={() => navigateToPage('shortlistedPlayers')}
              >
                اللاعبين المفضلين
              </div>
              <div 
                className={`cursor-pointer ml-6 mb-2 ${currentPage === 'notes' ? 'text-green-700 font-medium' : 'text-gray-600'}`}
                onClick={() => {
                  console.log("Clicking on notes menu item");
                  navigateToPage('notes');
                }}
              >
                ملاحظاتي
              </div>
            </nav>
          </div>

          <div className="flex items-center">
            <button 
              className={`p-2 mr-2 ${currentPage === 'shortlistedPlayers' ? 'text-green-700' : 'text-gray-600'}`}
              onClick={() => navigateToPage('shortlistedPlayers')}
              title="اللاعبين المفضلين"
            >
              <Star size={20} />
            </button>
            <button 
              className={`p-2 mr-2 ${currentPage === 'calendar' ? 'text-green-700' : 'text-gray-600'}`}
              onClick={() => navigateToPage('calendar')}
              title="جدول المباريات"
            >
              <Calendar size={20} />
            </button>
            <button 
              className={`p-2 mr-2 ${currentPage === 'notes' ? 'text-green-700' : 'text-gray-600'}`}
              onClick={() => {
                console.log("Clicking on notes icon");
                navigateToPage('notes');
              }}
              title="ملاحظاتي"
            >
              <BookOpen size={20} />
            </button>
            <button className="p-2 mr-2">
              <Bell size={20} />
            </button>
            <button className="p-2">
              <User size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
