import React from 'react';

const StatsRadarChart = ({ attributes, data, compareData }) => {
  // Determine if this is a comparison chart
  const isComparisonChart = !!compareData;

  // Use either provided data or attributes in the appropriate format
  const primaryData = data || (attributes ? attributes.map(attr => ({ name: attr.name, value: attr.value })) : []);

  // Calculate the coordinates for a radar chart point
  const calculatePoint = (value, total, angle) => {
    // Scale value between 0 and 1 (assuming values are between 0-100)
    const scaledValue = Math.min(value, 100) / 100;
    // Calculate distance from center based on value (0-1)
    const distance = scaledValue * 80;
    // Convert angle to radians
    const radians = angle * (Math.PI / 180);
    // Calculate x,y coordinates (center is at 100,100)
    const x = 100 + distance * Math.sin(radians);
    const y = 100 - distance * Math.cos(radians);
    return { x, y };
  };

  // Generate points for the radar chart polygon
  const generatePolygonPoints = (dataPoints) => {
    if (!dataPoints || dataPoints.length === 0) return '';

    // Calculate angle step based on number of data points
    const angleStep = 360 / dataPoints.length;

    // Generate points string
    return dataPoints.map((item, index) => {
      const angle = index * angleStep;
      const point = calculatePoint(item.value, 100, angle);
      return `${point.x},${point.y}`;
    }).join(' ');
  };

  // Primary data points
  const primaryPoints = generatePolygonPoints(primaryData);

  // Comparison data points (if provided)
  const comparisonPoints = isComparisonChart ? generatePolygonPoints(compareData) : '';

  // Generate the axis and labels
  const renderAxis = () => {
    const data = primaryData;
    if (!data || data.length === 0) return null;

    const angleStep = 360 / data.length;

    return data.map((item, index) => {
      const angle = index * angleStep;
      const endPoint = calculatePoint(100, 100, angle);

      // Calculate label position (slightly beyond the chart boundary)
      const labelPoint = calculatePoint(110, 100, angle);

      return (
        <React.Fragment key={index}>
          <line 
            x1="100" 
            y1="100" 
            x2={endPoint.x} 
            y2={endPoint.y} 
            stroke="#e5e7eb" 
            strokeWidth="1" 
          />
          <text 
            x={labelPoint.x} 
            y={labelPoint.y} 
            fontSize="10"
            fill="#4b5563"
            textAnchor="middle"
            dominantBaseline="middle"
          >
            {item.name}
          </text>
        </React.Fragment>
      );
    });
  };

  // Generate concentric circles for the background grid
  const renderGrid = () => {
    const circles = [20, 40, 60, 80];
    return circles.map((radius, index) => (
      <circle 
        key={index}
        cx="100" 
        cy="100" 
        r={radius} 
        fill="none" 
        stroke="#e5e7eb" 
        strokeWidth="1" 
        strokeDasharray="2,2"
      />
    ));
  };

  return (
    <div className="w-full h-full relative">
      <svg viewBox="0 0 200 200" className="w-full h-full">
        {/* Background grid */}
        {renderGrid()}

        {/* Axes */}
        {renderAxis()}

        {/* Second player polygon - draw this FIRST so it appears behind the primary one */}
        {isComparisonChart && comparisonPoints && (
          <polygon 
            points={comparisonPoints} 
            fill="rgba(34, 197, 94, 0.15)" 
            stroke="#10b981" 
            strokeWidth="2.5" 
            strokeLinejoin="round"
          />
        )}

        {/* Main player polygon - draw this SECOND so it appears on top */}
        {primaryPoints && (
          <polygon 
            points={primaryPoints} 
            fill={isComparisonChart ? "rgba(59, 130, 246, 0.1)" : "rgba(16, 185, 129, 0.15)"} 
            stroke={isComparisonChart ? "#3b82f6" : "#10b981"} 
            strokeWidth="2.5" 
            strokeLinejoin="round"
            strokeDasharray={isComparisonChart ? "none" : "none"}
          />
        )}

        {/* Center point */}
        <circle 
          cx="100" 
          cy="100" 
          r="2" 
          fill="#6b7280" 
        />

        {/* Legend for comparison */}
        {isComparisonChart && (
          <g>
            <rect x="130" y="15" width="12" height="12" fill="rgba(59, 130, 246, 0.1)" stroke="#3b82f6" strokeWidth="2" />
            <text x="148" y="24" fontSize="8" fill="#4b5563" textAnchor="start" dominantBaseline="middle">{data && data[0] ? data[0].player : 'لاعب 1'}</text>

            <rect x="130" y="35" width="12" height="12" fill="rgba(34, 197, 94, 0.15)" stroke="#10b981" strokeWidth="2" />
            <text x="148" y="44" fontSize="8" fill="#4b5563" textAnchor="start" dominantBaseline="middle">{compareData && compareData[0] ? compareData[0].player : 'لاعب 2'}</text>
          </g>
        )}
      </svg>
    </div>
  );
};

export default StatsRadarChart;
