import React from 'react';
import { Bell, User, Star, Calendar, BookOpen } from 'lucide-react';
import logo from '../logo/nujoom-logo.png';

const Header = ({ navigateToPage, currentPage }) => {
  console.log("Current page:", currentPage); // Debug current page

  return (
    <div className="bg-black bg-opacity-90 w-full z-50">
      <div className="container mx-auto px-4 py-2 flex justify-between items-center">
        <div className="flex items-center">
          <div className="text-white flex flex-row items-center">
            <User size={20} className="ml-4" />
            <Bell size={20} className="ml-4" />
            <BookOpen size={20} className="ml-4" />
            <Calendar size={20} className="ml-4" />
            <Star size={20} className="ml-4" />
          </div>
        </div>

        <div className="flex items-center justify-center space-x-6 space-x-reverse">
          <div 
            className={`cursor-pointer text-sm ${currentPage === 'notes' ? 'text-white font-medium' : 'text-gray-300'}`}
            onClick={() => navigateToPage('notes')}
          >
            ملاحظاتي
          </div>
          <div 
            className={`cursor-pointer text-sm ${currentPage === 'shortlistedPlayers' ? 'text-white font-medium' : 'text-gray-300'}`}
            onClick={() => navigateToPage('shortlistedPlayers')}
          >
            اللاعبين المفضلين
          </div>
          <div 
            className={`cursor-pointer text-sm ${currentPage === 'calendar' ? 'text-white font-medium' : 'text-gray-300'}`}
            onClick={() => navigateToPage('calendar')}
          >
            جدول المباريات
          </div>
          <div 
            className={`cursor-pointer text-sm ${currentPage === 'comparisons' ? 'text-white font-medium' : 'text-gray-300'}`}
            onClick={() => navigateToPage('comparisons')}
          >
            المقارنات
          </div>
          <div 
            className={`cursor-pointer text-sm ${currentPage === 'playersPage' ? 'text-white font-medium' : 'text-gray-300'}`}
            onClick={() => navigateToPage('playersPage')}
          >
            المواهب
          </div>
        </div>

        <div 
          className="cursor-pointer text-white text-xl font-bold"
          onClick={() => navigateToPage('mainPage')}
        >
          نجوم
        </div>
      </div>
    </div>
  );
};

export default Header;
