import React from 'react';
import { ArrowRight } from 'lucide-react';
import { motion } from 'framer-motion';

const BackButton = ({ navigateToPage, destination = 'dashboard', text = '' }) => {
  return (
    <motion.button 
      className="flex items-center justify-center bg-white border border-gray-200 rounded-lg p-2.5 mx-2 text-gray-600 hover:text-white hover:bg-[#032425] hover:border-[#032425] transition-all shadow-sm"
      onClick={() => {
        if (destination === 'dashboard' || destination === 'mainPage') {
          const event = new CustomEvent('animateToHome');
          window.dispatchEvent(event);
        }
        navigateToPage(destination);
      }}
      whileHover={{ x: -5 }}
      whileTap={{ scale: 0.95 }}
      initial={{ x: -20, opacity: 0 }}
      animate={{ x: 0, opacity: 1 }}
      transition={{ duration: 0.3, type: "spring", stiffness: 300 }}
    >
      <ArrowRight size={24} />
      {text && <span className="ml-2 font-medium">{text}</span>}
      {!text && <span className="ml-2 font-medium">العودة</span>}
    </motion.button>
  );
};

export default BackButton;
