import React from 'react';

const PerformanceTrendChart = ({ performanceData }) => {
  // Default data if none provided
  const defaultData = [
    { date: '01/10', rating: 7.2 },
    { date: '08/10', rating: 6.8 },
    { date: '15/10', rating: 7.4 },
    { date: '22/10', rating: 8.1 },
    { date: '29/10', rating: 7.9 },
    { date: '05/11', rating: 8.3 },
    { date: '12/11', rating: 8.5 },
  ];

  const data = performanceData || defaultData;

  // Chart dimensions
  const width = 100;
  const height = 100;
  const padding = 10;
  const chartWidth = width - (padding * 2);
  const chartHeight = height - (padding * 2);

  // Calculate min/max values
  const maxValue = Math.max(...data.map(d => d.rating));
  const minValue = Math.min(...data.map(d => d.rating));

  // Normalize to chart height (inverted y-axis)
  const normalize = (value) => {
    const range = maxValue - minValue;
    const adjustedRange = range < 2 ? 2 : range; // Ensure we have at least a range of 2 for visibility
    return chartHeight - ((value - minValue) / adjustedRange) * chartHeight + padding;
  };

  // Generate the path for the line chart
  const generatePath = () => {
    return data.map((point, index) => {
      const x = (index / (data.length - 1)) * chartWidth + padding;
      const y = normalize(point.rating);
      return `${index === 0 ? 'M' : 'L'} ${x} ${y}`;
    }).join(' ');
  };

  // Generate dots for each data point
  const generateDots = () => {
    return data.map((point, index) => {
      const x = (index / (data.length - 1)) * chartWidth + padding;
      const y = normalize(point.rating);

      // Determine color based on rating - updated color scheme
      const getColor = (rating) => {
        if (rating >= 90) return "#10b981"; // Green for 90+
        if (rating >= 80) return "#ffce00"; // Bright yellow/gold for 80-89
        return "#f59e0b"; // Yellow for below 80
      };

      return (
        <g key={index}>
          <circle
            cx={x}
            cy={y}
            r={2.5}
            fill={getColor(point.rating)}
            stroke="white"
            strokeWidth="1"
          />
          {/* Only show every other label on small screens to avoid crowding */}
          {(index % 2 === 0 || data.length <= 4) && (
            <text
              x={x}
              y={height - 2}
              fontSize="6"
              textAnchor="middle"
              fill="#6b7280"
            >
              {point.date}
            </text>
          )}
          {/* Rating label */}
          {(index === 0 || index === data.length - 1 || point.rating === maxValue || point.rating === minValue) && (
            <text
              x={x}
              y={y - 5}
              fontSize="6"
              textAnchor="middle"
              fill="#6b7280"
              fontWeight={point.rating === maxValue ? "bold" : "normal"}
            >
              {point.rating.toFixed(1)}
            </text>
          )}
        </g>
      );
    });
  };

  return (
    <div className="w-full h-48 flex flex-col">
      <div className="flex-1 relative">
        <svg viewBox={`0 0 ${width} ${height}`} className="w-full h-full overflow-visible">
          {/* X and Y axis */}
          <line x1={padding} y1={height-padding} x2={width-padding} y2={height-padding} stroke="#e5e7eb" strokeWidth="0.5" />
          <line x1={padding} y1={padding} x2={padding} y2={height-padding} stroke="#e5e7eb" strokeWidth="0.5" />

          {/* Grid lines */}
          <line x1={padding} y1={normalize(7)} x2={width-padding} y2={normalize(7)} stroke="#e5e7eb" strokeWidth="0.2" strokeDasharray="1,1" />
          <line x1={padding} y1={normalize(8)} x2={width-padding} y2={normalize(8)} stroke="#e5e7eb" strokeWidth="0.2" strokeDasharray="1,1" />
          <line x1={padding} y1={normalize(9)} x2={width-padding} y2={normalize(9)} stroke="#e5e7eb" strokeWidth="0.2" strokeDasharray="1,1" />

          {/* The chart line */}
          <path
            d={generatePath()}
            fill="none"
            stroke="#10b981"
            strokeWidth="1.5"
            strokeLinecap="round"
            strokeLinejoin="round"
          />

          {/* Area under the line */}
          <path
            d={`${generatePath()} L ${width-padding} ${height-padding} L ${padding} ${height-padding} Z`}
            fill="url(#gradient)"
            opacity="0.2"
          />

          {/* Gradient definition */}
          <defs>
            <linearGradient id="gradient" x1="0%" y1="0%" x2="0%" y2="100%">
              <stop offset="0%" stopColor="#10b981" stopOpacity="1" />
              <stop offset="100%" stopColor="#10b981" stopOpacity="0.1" />
            </linearGradient>
          </defs>

          {/* Data points */}
          {generateDots()}

          {/* Y-axis labels */}
          <text x={padding-3} y={normalize(7)} fontSize="6" textAnchor="end" fill="#6b7280" dominantBaseline="middle">7</text>
          <text x={padding-3} y={normalize(8)} fontSize="6" textAnchor="end" fill="#6b7280" dominantBaseline="middle">8</text>
          <text x={padding-3} y={normalize(9)} fontSize="6" textAnchor="end" fill="#6b7280" dominantBaseline="middle">9</text>
        </svg>
      </div>
      <div className="text-center text-sm text-gray-500 mt-2">تقييم الأداء في المباريات الأخيرة</div>
    </div>
  );
};

export default PerformanceTrendChart;
