import { useState, useEffect } from 'react';
import { Grid, List, ArrowLeft } from 'lucide-react';
import PlayerCard from '../components/PlayerCard';
import { players } from '../data/players';
import footballVideo from '../videos/football-intro.mp4';
import logo from '../logo/nujoom-logo.png';

const MainPage = ({ navigateToPage }) => {
  const [viewMode, setViewMode] = useState('grid');

  // Select only 5 players with the highest success rates
  const featuredPlayers = [...players]
    .sort((a, b) => b.successRate - a.successRate)
    .slice(0, 5);

  // Clean up any existing Watson Assistant elements (but keep Tawk.to)
  useEffect(() => {
    // Find and remove the Watson Assistant elements
    const watsonElements = document.querySelectorAll('[id^="WACContainer"]');
    watsonElements.forEach(el => el.remove());

    // Remove the Watson Assistant script
    const scriptElement = document.querySelector('script[src*="WatsonAssistantChatEntry.js"]');
    if (scriptElement) scriptElement.remove();
  }, []);

  const handlePlayerClick = (player) => {
    // Store the source page in sessionStorage
    sessionStorage.setItem('playerDetailsSource', 'mainPage');

    // Navigate to player details
    navigateToPage('playerDetails', player);
  };

  return (
    <div className="bg-[#F5F7FA]"> {/* Distant cloud as background */}
      {/* Video Welcome Section */}
      <div className="relative mb-10 overflow-hidden" style={{ height: '70vh', width: '100vw', marginLeft: 'calc(-50vw + 50%)', marginRight: 'calc(-50vw + 50%)' }}>
        {/* Video Background */}
        <video 
          className="absolute w-full h-full object-cover"
          autoPlay 
          muted 
          loop
          playsInline
          src={footballVideo}
        >
          Your browser does not support the video tag.
        </video>

        {/* Overlay with content */}
        <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col items-center justify-center text-center p-6">
          <img src={logo} alt="نجوم" className="h-48 mb-8" />
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">مرحباً بك في منصة نجوم</h1>
          <p className="text-xl md:text-2xl text-white mb-8 max-w-3xl">
            منصة اكتشاف المواهب الكروية في المملكة العربية السعودية
          </p>
          <button 
            onClick={() => navigateToPage('playersPage')}
            className="bg-[#E94E37] hover:bg-[#D03A27] text-white text-lg px-8 py-3 rounded-full transition-colors duration-300 flex items-center"
          >
            ابدأ الاستكشاف الآن
            <ArrowLeft size={20} className="mr-2" />
          </button>
        </div>
      </div>

      {/* About Section */}
      <div className="bg-[#F0E9E2] p-8 rounded-lg shadow-sm mb-10"> {/* Cocoon background */}
        <h2 className="text-2xl font-bold mb-6 text-[#333333]">عن المنصة</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div>
            <p className="mb-4 text-[#333333]">
              منصة <span className="font-bold text-[#E94E37]">نجوم</span> هي منصة متخصصة في اكتشاف المواهب الكروية الخفية في المملكة العربية السعودية، تهدف إلى إبراز اللاعبين الموهوبين الذين لم يحظوا بالاهتمام الكافي.
            </p>
            <p className="mb-4 text-[#333333]">
              تستخدم المنصة تقنيات الذكاء الاصطناعي المتقدمة لتحليل مقاطع الفيديو من المباريات واستخراج البيانات والإحصائيات بشكل آلي، مما يساعد في اكتشاف المواهب التي قد تمر دون ملاحظة بالطرق التقليدية.
            </p>
            <p className="text-[#333333]">
              تتميز منصة نجوم بارتباطها المباشر مع <span className="font-bold">SAFF+</span> (الاتحاد السعودي لكرة القدم)، مما يضمن تحديث البيانات بشكل مستمر وفوري، ويتيح للمواهب الشابة فرصة الظهور أمام المدربين والكشافين.
            </p>
          </div>
          <div>
            <h3 className="text-xl font-bold mb-4 text-[#333333]">مميزات المنصة</h3>
            <ul className="space-y-2">
              <li className="flex items-start">
                <span className="bg-[#E94E37] text-white rounded-full w-6 h-6 flex items-center justify-center mr-2 mt-1">1</span>
                <span className="text-[#333333]">اكتشاف المواهب الخفية باستخدام تقنيات الذكاء الاصطناعي</span>
              </li>
              <li className="flex items-start">
                <span className="bg-[#E94E37] text-white rounded-full w-6 h-6 flex items-center justify-center mr-2 mt-1">2</span>
                <span className="text-[#333333]">تحليل شامل لأداء اللاعبين وإبراز نقاط القوة لديهم</span>
              </li>
              <li className="flex items-start">
                <span className="bg-[#E94E37] text-white rounded-full w-6 h-6 flex items-center justify-center mr-2 mt-1">3</span>
                <span className="text-[#333333]">منصة تواصل بين المواهب والأندية والمدربين</span>
              </li>
              <li className="flex items-start">
                <span className="bg-[#E94E37] text-white rounded-full w-6 h-6 flex items-center justify-center mr-2 mt-1">4</span>
                <span className="text-[#333333]">تكامل مباشر مع بيانات SAFF+ لضمان الدقة والمصداقية</span>
              </li>
              <li className="flex items-start">
                <span className="bg-[#E94E37] text-white rounded-full w-6 h-6 flex items-center justify-center mr-2 mt-1">5</span>
                <span className="text-[#333333]">فرصة للاعبين من مختلف المناطق للظهور على الساحة الكروية</span>
              </li>
            </ul>
          </div>
        </div>
      </div>

      {/* Stats Section */}
      <div className="bg-white p-8 rounded-lg shadow-sm mb-10">
        <h2 className="text-2xl font-bold mb-8 text-center text-[#333333]">أرقام تتحدث عنا</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 text-center">
          <div className="p-6 bg-[#F5F7FA] rounded-lg transition-transform hover:scale-105 duration-300 border border-[#E0E5EB]">
            <div className="text-4xl font-bold text-[#E94E37] mb-2">+27000</div>
            <div className="text-[#555555]">لاعب موهوب</div>
          </div>
          <div className="p-6 bg-[#F5F7FA] rounded-lg transition-transform hover:scale-105 duration-300 border border-[#E0E5EB]">
            <div className="text-4xl font-bold text-[#E94E37] mb-2">+500</div>
            <div className="text-[#555555]">نادي شريك</div>
          </div>
        </div>
      </div>

      {/* Featured Players Section */}
      <div className="mb-10 bg-white p-8 rounded-lg shadow-sm">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-[#333333]">المواهب البارزة</h2>
          <div className="flex space-x-2 space-x-reverse">
            <button 
              className={`p-2 rounded ${viewMode === 'grid' ? 'bg-[#E94E37] text-white' : 'bg-[#F0E9E2] text-[#555555]'}`}
              onClick={() => setViewMode('grid')}
            >
              <Grid size={20} />
            </button>
            <button 
              className={`p-2 rounded ${viewMode === 'list' ? 'bg-[#E94E37] text-white' : 'bg-[#F0E9E2] text-[#555555]'}`}
              onClick={() => setViewMode('list')}
            >
              <List size={20} />
            </button>
          </div>
        </div>

        <div className={viewMode === 'grid' ? 'grid grid-cols-1 md:grid-cols-3 gap-6' : 'space-y-4'}>
          {featuredPlayers.map(player => (
            <PlayerCard
              key={player.id}
              player={player}
              onClick={() => handlePlayerClick(player)}
            />
          ))}
        </div>

        <div className="mt-8 text-center">
          <button 
            onClick={() => navigateToPage('playersPage')}
            className="bg-[#E94E37] text-white px-6 py-2 rounded-md hover:bg-[#D03A27] transition-colors duration-200"
          >
            عرض جميع المواهب
          </button>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
