import React, { useState, useEffect } from 'react';
import { ArrowRight, Star, StarOff, ChevronDown, ChevronUp, Calendar, Video, Edit, Plus, Trash, Save, Clock, MapPin, User } from 'lucide-react';
import { motion } from 'framer-motion';
import StatsRadarChart from '../components/StatsRadarChart';

const PlayerDetailsPage = ({ player, navigateToPage }) => {
  const [shortlistedPlayers, setShortlistedPlayers] = useState([]);
  const [showAllAttributes, setShowAllAttributes] = useState(false);
  const [activeTab, setActiveTab] = useState('upcoming');
  const [notes, setNotes] = useState(player?.notes || []);
  const [newNote, setNewNote] = useState('');
  const [editingNoteId, setEditingNoteId] = useState(null);
  const [editingNoteText, setEditingNoteText] = useState('');
  const [sourcePage, setSourcePage] = useState('mainPage');

  // Default stats if none are provided
  const defaultStats = [
    { name: "السرعة", value: 85 },
    { name: "التمرير", value: 80 },
    { name: "التسديد", value: 75 },
    { name: "المراوغة", value: 82 },
    { name: "القوة البدنية", value: 78 },
    { name: "الدقة", value: 77 },
    { name: "التمركز", value: 79 },
    { name: "الرؤية", value: 83 }
  ];

  // Sample upcoming games
  const upcomingGames = [
    { id: 1, date: '2025-04-05', title: 'الهلال vs النصر', time: '19:00', location: 'استاد الملك فهد الدولي' },
    { id: 2, date: '2025-04-12', title: 'الأهلي vs الهلال', time: '20:30', location: 'استاد الجوهرة' }
  ];

  // Sample highlights
  const highlights = [
    { id: 1, title: 'هدف رائع ضد النصر', date: '2025-03-10', videoUrl: '#' },
    { id: 2, title: 'مهارات فردية مميزة', date: '2025-02-25', videoUrl: '#' },
    { id: 3, title: 'تمريرة حاسمة في مباراة الأهلي', date: '2025-03-05', videoUrl: '#' }
  ];

  // Load shortlisted players from localStorage on component mount and when player changes
  useEffect(() => {
    const loadShortlist = () => {
      try {
        const savedShortlist = localStorage.getItem('shortlistedPlayers');
        console.log("PlayerDetailsPage - Loading shortlist from localStorage:", savedShortlist);

        if (savedShortlist) {
          const parsedShortlist = JSON.parse(savedShortlist);
          console.log("PlayerDetailsPage - Parsed shortlist:", parsedShortlist);
          setShortlistedPlayers(parsedShortlist);
        }
      } catch (e) {
        console.error("PlayerDetailsPage - Error loading shortlisted players:", e);
      }
    };

    // Load initially
    loadShortlist();

    // Set up event listener for storage changes
    const handleStorageChange = (e) => {
      console.log("PlayerDetailsPage - Storage event detected:", e);
      if (e.key === 'shortlistedPlayers' || e.key === null) {
        loadShortlist();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Also listen for our custom event
    window.addEventListener('shortlistUpdated', loadShortlist);

    // Clean up
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('shortlistUpdated', loadShortlist);
    };
  }, [player]); // Re-run when player changes

  // Check if the current player is shortlisted
  const isShortlisted = player ? shortlistedPlayers.some(p => p.id === player.id) : false;

  // Toggle shortlist status for the current player
  const toggleShortlist = () => {
    if (!player) return;

    console.log("PlayerDetailsPage - Toggling shortlist for player:", player.name);
    let newShortlist;

    if (isShortlisted) {
      newShortlist = shortlistedPlayers.filter(p => p.id !== player.id);
      console.log("PlayerDetailsPage - Removing player from shortlist");
    } else {
      newShortlist = [...shortlistedPlayers, player];
      console.log("PlayerDetailsPage - Adding player to shortlist");
    }

    console.log("PlayerDetailsPage - New shortlist:", newShortlist);

    // Update state
    setShortlistedPlayers(newShortlist);

    // Save to localStorage
    try {
      const jsonString = JSON.stringify(newShortlist);
      console.log("PlayerDetailsPage - Saving to localStorage:", jsonString);
      localStorage.setItem('shortlistedPlayers', jsonString);

      // Dispatch custom event to notify other components
      const event = new CustomEvent('shortlistUpdated');
      window.dispatchEvent(event);
    } catch (e) {
      console.error("PlayerDetailsPage - Error saving shortlist to localStorage:", e);
    }
  };

  // Determine the source page when component mounts
  useEffect(() => {
    // Check if we have a referrer in sessionStorage
    const storedSource = sessionStorage.getItem('playerDetailsSource');

    if (storedSource) {
      setSourcePage(storedSource);
    }
  }, []);

  // Handle back button click
  const handleBackClick = () => {
    navigateToPage(sourcePage);
  };

  // If no player is provided, show a message
  if (!player) {
    return (
      <div className="text-center py-10">
        <p className="text-gray-500 mb-4">بيانات اللاعب غير متوفرة</p>
        <button 
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg transition duration-300"
          onClick={() => navigateToPage('playersPage')}
        >
          الرجوع إلى قائمة اللاعبين
        </button>
      </div>
    );
  }

  // Ensure player has attributes
  const playerAttributes = player.attributes || defaultStats;

  const addNote = () => {
    if (!newNote.trim()) return;

    const note = {
      id: Date.now(),
      text: newNote,
      date: new Date().toISOString()
    };

    setNotes([...notes, note]);
    setNewNote('');
  };

  const deleteNote = (noteId) => {
    setNotes(notes.filter(note => note.id !== noteId));
  };

  const startEditingNote = (note) => {
    setEditingNoteId(note.id);
    setEditingNoteText(note.text);
  };

  const saveEditedNote = () => {
    if (!editingNoteText.trim()) return;

    setNotes(notes.map(note => 
      note.id === editingNoteId 
        ? { ...note, text: editingNoteText } 
        : note
    ));

    setEditingNoteId(null);
    setEditingNoteText('');
  };

  return (
    <div>
      <div className="mb-6 flex items-center">
        <button 
          onClick={handleBackClick}
          className="flex items-center text-gray-600 hover:text-gray-900"
        >
          <ArrowRight size={20} className="ml-1" />
          <span>رجوع</span>
        </button>
      </div>

      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="grid grid-cols-1 lg:grid-cols-3 gap-6"
      >
        <div className="lg:col-span-1">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-lg shadow overflow-hidden"
          >
            <div className="relative">
              <div className={`h-40 bg-gradient-to-b from-[#032425] to-[#599E68]`}></div>
              <div className="absolute -bottom-16 right-8">
                <div className="bg-white rounded-full p-2 shadow">
                  <img 
                    src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" 
                    alt={player.name} 
                    className="w-32 h-32 rounded-full"
                  />
                </div>
              </div>
            </div>

            <div className="pt-20 p-8">
              <div className="flex flex-wrap items-center mb-4">
                <h2 className="text-2xl font-bold text-gray-800 ml-3">{player.name}</h2>
                <div className="flex items-center space-x-2 space-x-reverse">
                  <span className="bg-[#E7F0EB] text-[#032425] text-sm font-medium px-2.5 py-0.5 rounded">
                    {player.position || 'لاعب'}
                  </span>
                  <span className="text-gray-500">
                    {player.age ? `${player.age} سنة` : ''}
                  </span>
                </div>
              </div>

              <div className="text-gray-600 mb-6">
                <p className="mb-2">
                  <span className="font-medium">النادي:</span> {player.club || 'غير محدد'}
                </p>
                {player.bio && (
                  <p className="mb-4">{player.bio}</p>
                )}
                {player.additionalInfo && (
                  <p className="text-gray-600">{player.additionalInfo}</p>
                )}
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
                <div>
                  <h3 className="font-bold text-gray-800 mb-4">الإحصائيات</h3>
                  <div className="space-y-3">
                    {(showAllAttributes ? playerAttributes : playerAttributes.slice(0, 3)).map((attr, index) => (
                      <div key={index} className="flex items-center">
                        <span className="w-28 text-right text-gray-600">{attr.name}</span>
                        <div className="flex-grow mx-3">
                          <div className="h-2 bg-gray-200 rounded-full">
                            <div 
                              className={`h-2 rounded-full ${
                                attr.value >= 85 ? 'bg-[#032425]' : 
                                attr.value >= 75 ? 'bg-[#266148]' : 
                                'bg-[#599E68]'
                              }`}
                              style={{ width: `${attr.value}%` }}
                            ></div>
                          </div>
                        </div>
                        <span className={`font-medium ${
                          attr.value >= 85 ? 'text-[#032425]' : 
                          attr.value >= 75 ? 'text-[#266148]' : 
                          'text-[#599E68]'
                        }`}>{attr.value}</span>
                      </div>
                    ))}

                    {playerAttributes.length > 3 && (
                      <button 
                        className="flex items-center text-[#032425] hover:text-[#266148] mt-2"
                        onClick={() => setShowAllAttributes(!showAllAttributes)}
                      >
                        <span>{showAllAttributes ? 'عرض أقل' : 'عرض المزيد'}</span>
                        {showAllAttributes ? (
                          <ChevronUp size={16} className="mr-1" />
                        ) : (
                          <ChevronDown size={16} className="mr-1" />
                        )}
                      </button>
                    )}
                  </div>
                </div>

                <div>
                  {player.strengths && player.strengths.length > 0 && (
                    <div className="mb-6">
                      <h3 className="font-bold text-gray-800 mb-3">نقاط القوة</h3>
                      <ul className="space-y-1">
                        {player.strengths.map((strength, index) => (
                          <li key={index} className="flex items-center">
                            <span className="w-2 h-2 bg-[#032425] rounded-full ml-2"></span>
                            <span>{strength}</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {player.weaknesses && player.weaknesses.length > 0 && (
                    <div>
                      <h3 className="font-bold text-gray-800 mb-3">نقاط الضعف</h3>
                      <ul className="space-y-1">
                        {player.weaknesses.map((weakness, index) => (
                          <li key={index} className="flex items-center">
                            <span className="w-2 h-2 bg-[#B91C1C] rounded-full ml-2"></span>
                            <span>{weakness}</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </motion.div>
        </div>

        <div className="lg:col-span-2">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            className="bg-white rounded-lg shadow overflow-hidden"
          >
            <div className="border-b border-gray-200">
              <nav className="flex">
                <button
                  className={`py-4 px-6 text-center border-b-2 font-medium text-sm ${
                    activeTab === 'upcoming'
                      ? 'border-[#032425] text-[#032425]'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                  onClick={() => setActiveTab('upcoming')}
                >
                  المباريات القادمة
                </button>
                <button
                  className={`py-4 px-6 text-center border-b-2 font-medium text-sm ${
                    activeTab === 'highlights'
                      ? 'border-[#032425] text-[#032425]'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                  onClick={() => setActiveTab('highlights')}
                >
                  أبرز اللقطات
                </button>
                <button
                  className={`py-4 px-6 text-center border-b-2 font-medium text-sm ${
                    activeTab === 'notes'
                      ? 'border-[#032425] text-[#032425]'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                  onClick={() => setActiveTab('notes')}
                >
                  الملاحظات
                </button>
              </nav>
            </div>

            <div className="p-6">
              {activeTab === 'upcoming' && (
                <div>
                  <h3 className="text-lg font-bold mb-4">المباريات القادمة</h3>
                  {upcomingGames.length === 0 ? (
                    <p className="text-gray-500 text-center py-4">لا توجد مباريات قادمة</p>
                  ) : (
                    <div className="space-y-4">
                      {upcomingGames.map(game => (
                        <div 
                          key={game.id}
                          className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition duration-300"
                        >
                          <h4 className="font-bold">{game.title}</h4>
                          <div className="flex items-center text-gray-600 text-sm mt-2">
                            <Calendar size={14} className="ml-1" />
                            <span className="ml-3">{new Date(game.date).toLocaleDateString('ar-SA')}</span>
                            <Clock size={14} className="mr-3 ml-1" />
                            <span className="ml-3">{game.time}</span>
                            <MapPin size={14} className="mr-3 ml-1" />
                            <span>{game.location}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}

              {activeTab === 'highlights' && (
                <div>
                  <h3 className="text-lg font-bold mb-4">أبرز اللقطات</h3>
                  {highlights.length === 0 ? (
                    <p className="text-gray-500 text-center py-4">لا توجد لقطات متاحة</p>
                  ) : (
                    <div className="space-y-4">
                      {highlights.map(highlight => (
                        <div 
                          key={highlight.id}
                          className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition duration-300"
                        >
                          <div className="flex items-center">
                            <div className="bg-gray-100 rounded-lg p-3 ml-4">
                              <Video size={24} className="text-gray-500" />
                            </div>
                            <div>
                              <h4 className="font-bold">{highlight.title}</h4>
                              <p className="text-gray-600 text-sm">{new Date(highlight.date).toLocaleDateString('ar-SA')}</p>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}

              {activeTab === 'notes' && (
                <div>
                  <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-bold">الملاحظات</h3>
                  </div>

                  <div className="mb-6">
                    <textarea
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500"
                      rows="3"
                      placeholder="أضف ملاحظة جديدة..."
                      value={newNote}
                      onChange={(e) => setNewNote(e.target.value)}
                    ></textarea>
                    <div className="flex justify-end mt-2">
                      <button 
                        className="bg-[#032425] hover:bg-[#143434] text-white py-2 px-4 rounded-lg transition duration-300 flex items-center"
                        onClick={addNote}
                      >
                        <Plus size={16} className="ml-1" />
                        إضافة ملاحظة
                      </button>
                    </div>
                  </div>

                  {notes.length === 0 ? (
                    <div className="text-center py-6 bg-gray-50 rounded-lg">
                      <p className="text-gray-500 mb-2">لا توجد ملاحظات بعد</p>
                      <p className="text-gray-400 text-sm">أضف ملاحظاتك الخاصة حول اللاعب</p>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {notes.map(note => (
                        <div 
                          key={note.id}
                          className="border border-gray-200 rounded-lg p-4"
                        >
                          {editingNoteId === note.id ? (
                            <div>
                              <textarea
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500"
                                rows="3"
                                value={editingNoteText}
                                onChange={(e) => setEditingNoteText(e.target.value)}
                              ></textarea>
                              <div className="flex justify-end mt-2 space-x-2 space-x-reverse">
                                <button 
                                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 py-1 px-3 rounded transition duration-300"
                                  onClick={() => setEditingNoteId(null)}
                                >
                                  إلغاء
                                </button>
                                <button 
                                  className="bg-[#032425] hover:bg-[#143434] text-white py-1 px-3 rounded transition duration-300 flex items-center"
                                  onClick={saveEditedNote}
                                >
                                  <Save size={16} className="ml-1" />
                                  حفظ
                                </button>
                              </div>
                            </div>
                          ) : (
                            <div>
                              <p className="text-gray-700">{note.text}</p>
                              <div className="flex justify-between items-center mt-3">
                                <span className="text-gray-500 text-sm">
                                  {new Date(note.date).toLocaleDateString('ar-SA')}
                                </span>
                                <div className="flex space-x-2 space-x-reverse">
                                  <button 
                                    className="text-gray-400 hover:text-blue-500 p-1"
                                    onClick={() => startEditingNote(note)}
                                  >
                                    <Edit size={16} />
                                  </button>
                                  <button 
                                    className="text-gray-400 hover:text-red-500 p-1"
                                    onClick={() => deleteNote(note.id)}
                                  >
                                    <Trash size={16} />
                                  </button>
                                </div>
                              </div>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}
            </div>
          </motion.div>
        </div>
      </motion.div>
    </div>
  );
};

export default PlayerDetailsPage;
