import React, { useState, useEffect } from 'react';
import { ArrowRight, Grid, List, Search, Star, StarOff, Filter, ChevronDown, ChevronUp, X } from 'lucide-react';
import { motion } from 'framer-motion';
import PlayerCard from '../components/PlayerCard';
import BackButton from '../components/BackButton';
// Import players directly
import { players } from '../data/players';

const PlayersPage = ({ navigateToPage }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'
  const [shortlistedPlayers, setShortlistedPlayers] = useState([]);
  const [showFilters, setShowFilters] = useState(false);

  // Filter states
  const [filters, setFilters] = useState({
    ageRange: [17, 21],
    height: [160, 190],
    weight: [60, 85],
    teams: [],
    positions: [],
    foot: [],
  });

  // Get unique values for filter options
  const teams = [...new Set(players.map(player => player.team))];
  const positions = [...new Set(players.map(player => player.position))];
  const footOptions = [...new Set(players.map(player => player.foot))];

  // Load shortlisted players from localStorage on component mount
  useEffect(() => {
    const loadShortlist = () => {
      try {
        const savedShortlist = localStorage.getItem('shortlistedPlayers');
        console.log("Loading shortlist from localStorage:", savedShortlist);

        if (savedShortlist) {
          const parsedShortlist = JSON.parse(savedShortlist);
          console.log("Parsed shortlist:", parsedShortlist);
          setShortlistedPlayers(parsedShortlist);
        }
      } catch (e) {
        console.error("Error loading shortlisted players:", e);
      }
    };

    // Load initially
    loadShortlist();

    // Set up event listener for storage changes
    const handleStorageChange = (e) => {
      console.log("Storage event detected:", e);
      if (e.key === 'shortlistedPlayers' || e.key === null) {
        loadShortlist();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Also listen for our custom event
    window.addEventListener('shortlistUpdated', loadShortlist);

    // Clean up
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('shortlistUpdated', loadShortlist);
    };
  }, []);

  // Handle filter changes
  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  // Reset all filters
  const resetFilters = () => {
    setFilters({
      ageRange: [17, 21],
      height: [160, 190],
      weight: [60, 85],
      teams: [],
      positions: [],
      foot: [],
    });
    setSearchQuery('');
  };

  // Toggle filter visibility
  const toggleFilters = () => {
    setShowFilters(!showFilters);
  };

  // Toggle selection in array filters (teams, positions, foot)
  const toggleArrayFilter = (filterType, value) => {
    setFilters(prev => {
      const currentValues = [...prev[filterType]];

      if (currentValues.includes(value)) {
        return {
          ...prev,
          [filterType]: currentValues.filter(item => item !== value)
        };
      } else {
        return {
          ...prev,
          [filterType]: [...currentValues, value]
        };
      }
    });
  };

  // Apply all filters to players
  const filteredPlayers = players.filter(player => {
    // Text search filter
    const textMatch = 
      searchQuery === '' ||
      player.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
      (player.team && player.team.toLowerCase().includes(searchQuery.toLowerCase())) ||
      (player.position && player.position.toLowerCase().includes(searchQuery.toLowerCase()));

    // Age range filter
    const ageMatch = 
      player.age >= filters.ageRange[0] && 
      player.age <= filters.ageRange[1];

    // Height filter
    const heightMatch = 
      !player.height || 
      (player.height >= filters.height[0] && 
       player.height <= filters.height[1]);

    // Weight filter
    const weightMatch = 
      !player.weight || 
      (player.weight >= filters.weight[0] && 
       player.weight <= filters.weight[1]);

    // Team filter
    const teamMatch = 
      filters.teams.length === 0 || 
      (player.team && filters.teams.includes(player.team));

    // Position filter
    const positionMatch = 
      filters.positions.length === 0 || 
      (player.position && filters.positions.includes(player.position));

    // Foot preference filter
    const footMatch = 
      filters.foot.length === 0 || 
      (player.foot && filters.foot.includes(player.foot));

    return textMatch && ageMatch && heightMatch && weightMatch && teamMatch && positionMatch && footMatch;
  });

  // Check if a player is shortlisted
  const isPlayerShortlisted = (playerId) => {
    return shortlistedPlayers.some(player => player.id === playerId);
  };

  // Toggle shortlist status for a player
  const toggleShortlist = (player) => {
    console.log("Toggling shortlist for player:", player.name);
    let newShortlist;

    if (isPlayerShortlisted(player.id)) {
      newShortlist = shortlistedPlayers.filter(p => p.id !== player.id);
      console.log("Removing player from shortlist");
    } else {
      newShortlist = [...shortlistedPlayers, player];
      console.log("Adding player to shortlist");
    }

    console.log("New shortlist:", newShortlist);

    // Update state
    setShortlistedPlayers(newShortlist);

    // Save to localStorage
    try {
      const jsonString = JSON.stringify(newShortlist);
      console.log("Saving to localStorage:", jsonString);
      localStorage.setItem('shortlistedPlayers', jsonString);

      // Dispatch custom event to notify other components
      const event = new CustomEvent('shortlistUpdated');
      window.dispatchEvent(event);
    } catch (e) {
      console.error("Error saving shortlist to localStorage:", e);
    }
  };

  // Handle player card click
  const handlePlayerClick = (player) => {
    // Store the source page in sessionStorage
    sessionStorage.setItem('playerDetailsSource', 'playersPage');

    // Navigate to player details
    navigateToPage('playerDetails', player);
  };

  // Render range slider for filter
  const renderRangeSlider = (label, min, max, filterKey, step = 1, unit = '') => {
    return (
      <div className="mb-3">
        <div className="flex justify-between text-sm text-gray-600 mb-1">
          <span>{label}</span>
          <span>
            {filters[filterKey][0]}{unit} - {filters[filterKey][1]}{unit}
          </span>
        </div>
        <div className="flex space-x-2 space-x-reverse">
          <input 
            type="range" 
            min={min} 
            max={max} 
            step={step}
            value={filters[filterKey][0]}
            onChange={(e) => handleFilterChange(filterKey, [parseInt(e.target.value), filters[filterKey][1]])}
            className="w-1/2 accent-[#032425]"
          />
          <input 
            type="range" 
            min={min} 
            max={max} 
            step={step}
            value={filters[filterKey][1]}
            onChange={(e) => handleFilterChange(filterKey, [filters[filterKey][0], parseInt(e.target.value)])}
            className="w-1/2 accent-[#032425]"
          />
        </div>
      </div>
    );
  };

  return (
    <div>
      <BackButton 
        navigateToPage={navigateToPage} 
        destination="dashboard" 
        title="اللاعبين" 
      />

      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="bg-white rounded-lg shadow-sm p-4 mb-6">
          <div className="flex justify-between items-center">
            <div className="flex space-x-2 space-x-reverse">
              <button 
                className={`p-2 rounded-lg ${viewMode === 'grid' ? 'bg-blue-100 text-blue-600' : 'text-gray-500 hover:bg-gray-100'}`}
                onClick={() => setViewMode('grid')}
              >
                <Grid size={20} />
              </button>
              <button 
                className={`p-2 rounded-lg ${viewMode === 'list' ? 'bg-blue-100 text-blue-600' : 'text-gray-500 hover:bg-gray-100'}`}
                onClick={() => setViewMode('list')}
              >
                <List size={20} />
              </button>
              <button 
                className={`p-2 rounded-lg ${showFilters ? 'bg-[#E7F0EB] text-[#032425]' : 'text-gray-500 hover:bg-gray-100'}`}
                onClick={toggleFilters}
              >
                <Filter size={20} />
              </button>
            </div>
            <div className="relative flex-grow mx-4">
              <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
                <Search size={20} className="text-gray-400" />
              </div>
              <input 
                type="text" 
                className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full pr-10 p-2.5 text-right" 
                placeholder="البحث عن لاعب..." 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              {searchQuery && (
                <button 
                  className="absolute inset-y-0 left-0 flex items-center pl-3"
                  onClick={() => setSearchQuery('')}
                >
                  <X size={16} className="text-gray-400" />
                </button>
              )}
            </div>
          </div>

          {/* Advanced Filters */}
          {showFilters && (
            <div className="mt-4 border-t pt-4">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-md font-medium text-gray-700">تصفية متقدمة</h3>
                <button 
                  className="text-sm text-[#032425] hover:underline"
                  onClick={resetFilters}
                >
                  إعادة ضبط
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  {/* Age Range Slider */}
                  {renderRangeSlider('العمر', 17, 21, 'ageRange', 1, ' سنة')}

                  {/* Height Range Slider */}
                  {renderRangeSlider('الطول', 160, 190, 'height', 1, ' سم')}

                  {/* Weight Range Slider */}
                  {renderRangeSlider('الوزن', 60, 85, 'weight', 1, ' كجم')}
                </div>

                <div>
                  {/* Team Filter */}
                  <div className="mb-3">
                    <h4 className="text-sm font-medium text-gray-600 mb-1">الفريق</h4>
                    <div className="flex flex-wrap gap-2">
                      {teams.map(team => (
                        <button
                          key={team}
                          className={`text-xs px-2 py-1 rounded ${
                            filters.teams.includes(team) 
                              ? 'bg-[#032425] text-white' 
                              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                          }`}
                          onClick={() => toggleArrayFilter('teams', team)}
                        >
                          {team}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Position Filter */}
                  <div className="mb-3">
                    <h4 className="text-sm font-medium text-gray-600 mb-1">المركز</h4>
                    <div className="flex flex-wrap gap-2">
                      {positions.map(position => (
                        <button
                          key={position}
                          className={`text-xs px-2 py-1 rounded ${
                            filters.positions.includes(position) 
                              ? 'bg-[#032425] text-white' 
                              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                          }`}
                          onClick={() => toggleArrayFilter('positions', position)}
                        >
                          {position}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Foot Preference */}
                  <div className="mb-3">
                    <h4 className="text-sm font-medium text-gray-600 mb-1">القدم المفضلة</h4>
                    <div className="flex flex-wrap gap-2">
                      {footOptions.map(foot => (
                        <button
                          key={foot}
                          className={`text-xs px-2 py-1 rounded ${
                            filters.foot.includes(foot) 
                              ? 'bg-[#032425] text-white' 
                              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                          }`}
                          onClick={() => toggleArrayFilter('foot', foot)}
                        >
                          {foot}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Results Count */}
        <div className="text-sm text-gray-600 mb-4 text-right">
          تم العثور على {filteredPlayers.length} لاعب
        </div>

        {filteredPlayers.length === 0 ? (
          <div className="text-center py-10 bg-white rounded-lg shadow-sm">
            <p className="text-gray-500">لا توجد نتائج مطابقة للبحث</p>
          </div>
        ) : (
          viewMode === 'list' ? (
            <div className="bg-white rounded-lg shadow-sm overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      اللاعب
                    </th>
                    <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      الفريق
                    </th>
                    <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      المركز
                    </th>
                    <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      العمر
                    </th>
                    <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      الطول
                    </th>
                    <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      المفضلة
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredPlayers.map(player => (
                    <tr key={player.id} className="hover:bg-gray-50 cursor-pointer" onClick={() => handlePlayerClick(player)}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div className="flex-shrink-0 h-10 w-10">
                            <img className="h-10 w-10 rounded-full" src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="" />
                          </div>
                          <div className="mr-4">
                            <div className="text-sm font-medium text-gray-900">{player.name}</div>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{player.team}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{player.position}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{player.age} سنة</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{player.height} سم</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        <button 
                          className="text-gray-400 hover:text-gray-900"
                          onClick={(e) => {
                            e.stopPropagation();
                            toggleShortlist(player);
                          }}
                        >
                          {isPlayerShortlisted(player.id) ? (
                            <Star size={20} className="text-yellow-400" />
                          ) : (
                            <StarOff size={20} className="text-gray-400" />
                          )}
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredPlayers.map(player => (
                <PlayerCard 
                  key={player.id}
                  player={player}
                  onClick={() => handlePlayerClick(player)}
                  onToggleShortlist={() => toggleShortlist(player)}
                  isShortlisted={isPlayerShortlisted(player.id)}
                  isHighlighted={player.successRate >= 85}
                />
              ))}
            </div>
          )
        )}
      </motion.div>
    </div>
  );
};

export default PlayersPage;
